&lt;li&gt;登录模块表单校验&lt;br /&gt;
（用户名必须大于等于 4 位，密码必须大于等于 6 位）&lt;br /&gt;
这里使用 element-plus 中 Form 组件自带的表单验证功能： &lt;code&gt;rules&lt;/code&gt;  属性&lt;br /&gt;
要保证所有的表单项校验通过再发请求  &lt;code&gt;validate&lt;/code&gt;&lt;/li&gt;
&lt;/ol&gt;
&lt;pre&gt;&lt;code class=&#34;language-vue&#34;&gt;&amp;lt;template&amp;gt;
  &amp;lt;el-form class=&amp;quot;login_form&amp;quot; :model=&amp;quot;loginForm&amp;quot; :rules=&amp;quot;rules&amp;quot; ref=&amp;quot;loginForms&amp;quot;&amp;gt;
    &amp;lt;h1&amp;gt;请登录&amp;lt;/h1&amp;gt;
    &amp;lt;el-form-item prop=&amp;quot;username&amp;quot;&amp;gt;
      &amp;lt;el-input :prefix-icon=&amp;quot;User&amp;quot; v-model=&amp;quot;loginForm.username&amp;quot;&amp;gt;&amp;lt;/el-input&amp;gt;
    &amp;lt;/el-form-item&amp;gt;
    &amp;lt;el-form-item prop=&amp;quot;password&amp;quot;&amp;gt;
      &amp;lt;el-input type=&amp;quot;password&amp;quot; :prefix-icon=&amp;quot;Lock&amp;quot; show-password=&amp;quot;true&amp;quot; v-model=&amp;quot;loginForm.password&amp;quot;&amp;gt;&amp;lt;/el-input&amp;gt;
    &amp;lt;/el-form-item&amp;gt;
  &amp;lt;/el-form&amp;gt;
&amp;lt;/template&amp;gt;
&amp;lt;script setup lang=&amp;quot;ts&amp;quot;&amp;gt;
//收集账号与密码的数据
let loginForm = reactive(&amp;#123;
  username: &amp;quot;admin&amp;quot;,
  password: &amp;quot;111111&amp;quot;,
&amp;#125;);
//获取el-form组件
let loginForms = ref();

const login = async () =&amp;gt; &amp;#123;
  //保证所有的表单项校验通过再发请求
  await loginForms.value.validate();
  //点击登录后干什么？
  //通知仓库发登录请求
  //请求成功-首页home
  //请求失败-弹出登录失败信息
  try &amp;#123;
    //保证登录成功
    await useStore.userLogin(loginForm);
    //编程式导航跳转到home首页
    $router.push(&amp;quot;/&amp;quot;);
    //登陆成功的提示信息
    ElNotification(&amp;#123;
      type: &amp;quot;success&amp;quot;,
      message: &amp;quot;欢迎回来&amp;quot;,
      title: `Hi,$&amp;#123;getTime()&amp;#125;好`,
    &amp;#125;);
  &amp;#125; catch (error) &amp;#123;
    ElNotification(&amp;#123;
      type: &amp;quot;error&amp;quot;,
      message: (error as Error).message,
    &amp;#125;);
  &amp;#125;
&amp;#125;;

//定义表单校验需要的配置对象
const rules = &amp;#123;
  username: [
    &amp;#123;
      required: true,
      min: 4,
      max: 10,
      message: &amp;quot;账号长度至少4位&amp;quot;,
      trigger: &amp;quot;change&amp;quot;,
    &amp;#125;,
  ],
  password: [
    &amp;#123;
      required: true,
      min: 6,
      max: 12,
      message: &amp;quot;密码长度至少6位&amp;quot;,
      trigger: &amp;quot;change&amp;quot;,
    &amp;#125;,
  ],
&amp;#125;;
