//获取父组件传递过来的全部路由数组
defineProps([&amp;quot;menuList&amp;quot;]);

//获取路由器对象
let $router = useRouter();
//点击菜单的回调,vc：传入的参数为组件实例
const goRoute = (vc: any) =&amp;gt; &amp;#123;
  $router.push(vc.index);
