&lt;li&gt;刷新功能实现&lt;br /&gt;
涉及到组件间的通信&lt;br /&gt;
在 layout 相关小仓库中的 state 中新声明一个变量，用于保存刷新按钮的状态，一旦点击刷新按钮，状态就会发生变化，而 main 路由组件一旦监听（watch）到其发生变化，就会进行路由组件销毁&lt;br /&gt;
 store/modules/setting.ts:&lt;/li&gt;
&lt;/ol&gt;
&lt;figure class=&#34;highlight ts&#34;&gt;&lt;figcaption data-lang=&#34;TypeScript&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token comment&#34;&gt;// 小仓库存储数据的地方&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;2&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;  &lt;span class=&#34;token function-variable function&#34;&gt;state&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;:&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;=&gt;&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;&amp;#123;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;3&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    &lt;span class=&#34;token keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;&amp;#123;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;4&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;      refresh&lt;span class=&#34;token operator&#34;&gt;:&lt;/span&gt;&lt;span class=&#34;token boolean&#34;&gt;false&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;&lt;span class=&#34;token comment&#34;&gt;// 用于控制刷新效果&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;5&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    &lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;layout/tabbar/index.vue:&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-vue&#34;&gt;&amp;lt;template&amp;gt;
  &amp;lt;!-- 刷新按钮 --&amp;gt;
  &amp;lt;el-button size=&amp;quot;small&amp;quot; icon=&amp;quot;Refresh&amp;quot; circle @click=&amp;quot;updateRefsh&amp;quot;&amp;gt;&amp;lt;/el-button&amp;gt;
&amp;lt;/template&amp;gt;

&amp;lt;script setup lang=&amp;quot;ts&amp;quot;&amp;gt;
//刷新按钮点击的回调
const updateRefsh = () =&amp;gt; &amp;#123;
  LayoutSettingStore.refresh = !LayoutSettingStore.refresh;
&amp;#125;;
&amp;lt;/script&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;layout/mian/index.vue&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-vue&#34;&gt;&amp;lt;template&amp;gt;
  &amp;lt;!-- 路由组件出口的位置 --&amp;gt;
  &amp;lt;router-view v-slot=&amp;quot;&amp;#123; Component &amp;#125;&amp;quot;&amp;gt;
    &amp;lt;transition name=&amp;quot;fade&amp;quot;&amp;gt;
      &amp;lt;!-- 渲染layout一级路由组件的子路由 --&amp;gt;
      &amp;lt;component :is=&amp;quot;Component&amp;quot; v-if=&amp;quot;flag&amp;quot; /&amp;gt;
    &amp;lt;/transition&amp;gt;
  &amp;lt;/router-view&amp;gt;
&amp;lt;/template&amp;gt;
&amp;lt;script setup lang=&amp;quot;ts&amp;quot;&amp;gt;
import &amp;#123; watch, ref, nextTick &amp;#125; from &amp;quot;vue&amp;quot;;
import useLayoutSettingStore from &amp;quot;@/store/modules/setting&amp;quot;;

//获取仓库
let LayoutSettingStore = useLayoutSettingStore();

//控制当前路由组件是否销毁重建
let flag = ref(true);

//监听仓库内部的数据是否发生变化，如果发生变化，说明用户点击过刷新按钮
watch(
  () =&amp;gt; LayoutSettingStore.refresh,
  () =&amp;gt; &amp;#123;
    //点击刷新按钮：路由组件销毁
    flag.value = false;
    //销毁完毕之后再创建
    nextTick(() =&amp;gt; &amp;#123;
      flag.value = true;
    &amp;#125;);
  &amp;#125;
);
&amp;lt;/script&amp;gt;
