  let result: any = await reqAddOrUpdateTrademark(trademarkParams);
  if (result.code == 200) &amp;#123;
    //弹出添加成功的信息；对话框关闭；重新发一次请求获取全部数据；清空trademarkParams数据（这个在“添加品牌”的按钮业务中做，每一次添加品牌前都清空数据）
    dialogFormVisible.value = false;
    ElMessage(&amp;#123;
      type: &amp;quot;success&amp;quot;,
      message: &amp;quot;添加品牌成功&amp;quot;,
    &amp;#125;);
    getHsaTrademark();
  &amp;#125; else &amp;#123;
    dialogFormVisible.value = false;
    ElMessage(&amp;#123;
      type: &amp;quot;error&amp;quot;,
      message: &amp;quot;添加品牌失败&amp;quot;,
    &amp;#125;);
  &amp;#125;
&amp;#125;;
&amp;lt;/script&amp;gt;
