import type &amp;#123; UserResponseData, Records &amp;#125; from &amp;quot;@/api/acl/user/type&amp;quot;;
//默认页码
let pageNo = ref&amp;lt;number&amp;gt;(1);
//一页展示几条数据
let pageSize = ref&amp;lt;number&amp;gt;(5);
//用户总数
let total = ref&amp;lt;number&amp;gt;(0);
//存储全部用户的数据
let userArr = ref&amp;lt;Records&amp;gt;([]);

