  //保存按钮：添加新用户或更新已有用户
  let result: any = await reqAddOrUpdateUser(userParams);
  if (result.code == 200) &amp;#123;
    drawer.value = false;
    ElMessage(&amp;#123;
      type: &amp;quot;success&amp;quot;,
      message: userParams.id ? &amp;quot;更新成功&amp;quot; : &amp;quot;添加成功&amp;quot;,
    &amp;#125;);
    getHasUser();
  &amp;#125; else &amp;#123;
    drawer.value = false;
