&lt;pre&gt;&lt;code class=&#34;language-methods&#34;&gt;changeOrder(flag) &amp;#123;
  //flag形参：它是一个标记，代表用户点击的是综合(1)还是价格(2)  用户点击的时候传递进来的
  let originOrder = this.searchParams.order
  //这里获取到的是最开始的状态
  let originFlag = this.searchParams.order.split(&amp;quot;:&amp;quot;)[0]
  let originSort = this.searchParams.order.split(&amp;quot;:&amp;quot;)[1]
  //准备一个新的order的属性值
  let newOrder = &#39;&#39;
  //确定点击的一定是“综合”
  if(flag == originFlag) &amp;#123;
    newOrder = `$&amp;#123;originFlag&amp;#125;:$&amp;#123;originSort==&amp;quot;desc&amp;quot;?&amp;quot;asc&amp;quot;:&amp;quot;desc&amp;quot;&amp;#125;`
  &amp;#125;else &amp;#123;
  //确定点击的一定是“价格”
  newOrder = `$&amp;#123;flag&amp;#125;:$&amp;#123;&amp;quot;desc&amp;quot;&amp;#125;`
  &amp;#125;
  //将newOrder赋予searchParams
  this.searchParams.order = newOrder
  //再次发请求
  this.getData()
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h1 id=&#34;分页器&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#分页器&#34;&gt;#&lt;/a&gt; 分页器&lt;/h1&gt;
&lt;ul&gt;
&lt;li&gt;为什么很多项目采用分页功能，因为有的平台同时展示的数据有很多&lt;/li&gt;
&lt;li&gt;对于分页器而言，自定义数据需要知道四个前提条件
&lt;ol&gt;
&lt;li&gt;pageNo：当前第几页&lt;/li&gt;
&lt;li&gt;pageSize：代表每一页展示多少条数据&lt;/li&gt;
&lt;li&gt;total：代表整个分页一共要展示多少条数据&lt;/li&gt;
&lt;li&gt;continues：代表分页连续页码个数&lt;/li&gt;
&lt;/ol&gt;
&lt;/li&gt;
&lt;li&gt;自定义分页器，在开发的时候先自己传递假的数据进行测试，调试成功后在用服务器数据&lt;/li&gt;
&lt;li&gt;对于分页器而言，很重要的一个地方即为【算出：连续页面起始数字和结束数字】&lt;/li&gt;
&lt;/ul&gt;
&lt;pre&gt;&lt;code&gt;export default &amp;#123;
  name: &amp;quot;Pagination&amp;quot;,
  props: [&amp;quot;pageNo&amp;quot;, &amp;quot;pageSize&amp;quot;, &amp;quot;total&amp;quot;, &amp;quot;continues&amp;quot;],
  computed: &amp;#123;
    //总共多少页
    totalPage() &amp;#123;
      //向上取整
      return Math.ceil(this.total / this.pageSize);
    &amp;#125;,
    //计算出连续页码的起始数字与结束数字[连续的页码数字，至少是5]
    startNumAndEndNum() &amp;#123;
      //解构
      const &amp;#123; continues, pageNo, totalPage &amp;#125; = this;
      //先定义两个变量存储起始数字与结束数字
      let start = 0,
        end = 0;
      //连续页码数字5【就是至少5页】，如果出现不正常现象(就是不够五页)
      //不正常现象：总页数没有连续页码多
      if (continues &amp;gt; totalPage) &amp;#123;
        start = 1;
        end = totalPage;
      &amp;#125; else &amp;#123;
        //正常现象
        //起始数字
        start = pageNo - parseInt(continues / 2);
        //结束数字
        end = pageNo + parseInt(continues / 2);
        //把出现的不正常现象(start数字出现0或负数)纠正
        if (start &amp;lt; 1) &amp;#123;
          start = 1;
          end = continues;
        &amp;#125;
        //把出现的不正常现象(end数字大于总页码)纠正
        if (end &amp;gt; totalPage) &amp;#123;
          end = totalPage;
          start = totalPage - continues + 1;
        &amp;#125;
      &amp;#125;
      return &amp;#123; start, end &amp;#125;;
    &amp;#125;,
  &amp;#125;,
&amp;#125;;
&lt;/code&gt;&lt;/pre&gt;
